import { createStore } from 'vuex';
import firebase from 'firebase'

export default createStore({
  state: {
    loadedParties: [
      {partyName: 'party',
      activities: ['cycling', 'picnic'],
      partyDate: new Date() }
    ]
  },
  getters: {
  },
  mutations: {
  },
  actions: {
    createParty({commit}, payload) {
      const party = {
        name: payload.partyName,
        activities: payload.activies,
        date: payload.partyDate
      }
      firebase.database().ref('parties').push(party)
      .then((data) => {
        console.log(data)
      })
      .catch((error) => {
        console.log(error)
      })
      commit('createParty', party)
    }
  },
  modules: {
  }
})
