import { createStore } from 'vuex';
import firebase from 'firebase'

export default createStore({
  state: {
  },
  getters: {
  },
  mutations: {
  },
  actions: {
    createParty({commit}, payload) {
      const party = {
        name: payload.partyName,
        activities: payload.activies,
        date: payload.partyName
      }
      firebase.database().ref('partie')
      commit('createParty', party)
    }
  },
  modules: {
  }
})
